import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';

import '../../Models/Product.dart';

class Business {
  final String name;
  final int totalProducts;
  final DateTime date;
  final List<Product> products;

  Business({
    required this.name,
    required this.totalProducts,
    required this.date,
    required this.products,
  });
}



class BusinessesListScreen extends StatelessWidget {
  final List<Business> businesses = [
    Business(
      name: "Tech Store",
      totalProducts: 5,
      date: DateTime.now(),
      products: [
        Product(name: "Laptop",
            price: 1200,
            imageUrl: "data:image/jpeg;base64,"),
        Product(name: "Phone",
            price: 800,
            imageUrl: "data:image/jpeg;base64,"),
      ],
    ),
    Business(
      name: "Foodies",
      totalProducts: 3,
      date: DateTime.now().subtract(Duration(days: 2)),
      products: [
        Product(name: "Burger",
            price: 5,
            imageUrl: "data:image/jpeg;base64,"),
        Product(name: "Pizza",
            price: 10,
            imageUrl: "data:image/jpeg;base64,"),
      ],
    ),
  ];

  BusinessesListScreen({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Column(
        children: [
          Container(
            padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 8),
            color: Colors.blue.shade50,
            child: Row(
              children: const [
                Expanded(flex: 3,
                    child: Text(
                        "Name", style: TextStyle(fontWeight: FontWeight.bold))),
                Expanded(flex: 2,
                    child: Text("Total Products",
                        style: TextStyle(fontWeight: FontWeight.bold))),
                Expanded(flex: 2,
                    child: Text(
                        "Date", style: TextStyle(fontWeight: FontWeight.bold))),
                Expanded(flex: 3,
                    child: Text("Actions",
                        style: TextStyle(fontWeight: FontWeight.bold))),
              ],
            ),
          ),
          Expanded(
            child: ListView.builder(
              itemCount: businesses.length,
              itemBuilder: (context, index) {
                final business = businesses[index];
                return InkWell(
                  onTap: () => _showProductsBottomSheet(context, business),
                  child: Container(
                    padding: const EdgeInsets.symmetric(
                        horizontal: 16, vertical: 12),
                    decoration: BoxDecoration(
                      border: Border(bottom: BorderSide(color: Colors.grey
                          .shade300)),
                    ),
                    child: Row(
                      children: [
                        Expanded(flex: 3, child: Text(business.name)),
                        Expanded(flex: 2, child: Text(
                            business.totalProducts.toString())),
                        Expanded(flex: 2, child: Text(
                            "${business.date.day}/${business.date
                                .month}/${business.date.year}")),
                        Expanded(
                          flex: 3,
                          child: Row(
                            children: [
                              IconButton(
                                icon: const Icon(
                                    Icons.edit, color: Colors.blue),
                                onPressed: () {
                                  // TODO: handle edit
                                },
                              ),
                              IconButton(
                                icon: const Icon(
                                    Icons.delete, color: Colors.red),
                                onPressed: () =>
                                    _showDeleteDialog(context, business),
                              ),
                            ],
                          ),
                        ),
                      ],
                    ),
                  ),
                );
              },
            ),
          ),
        ],
      ),
    );
  }

  void _showDeleteDialog(BuildContext context, Business business) {
    showDialog(
      context: context,
      builder: (context) =>
          AlertDialog(
            title: const Text("Confirm Delete"),
            content: Text("Are you sure you want to delete ${business.name}?"),
            actions: [
              TextButton(
                onPressed: () => Navigator.pop(context),
                child: const Text("Cancel"),
              ),
              TextButton(
                onPressed: () {
                  // TODO: handle deletion
                  Navigator.pop(context);
                },
                child: const Text(
                    "Delete", style: TextStyle(color: Colors.red)),
              ),
            ],
          ),
    );
  }

  void _showProductsBottomSheet(BuildContext context, Business business) {
    showModalBottomSheet(
      context: context,
      isScrollControlled: true,
      backgroundColor: Colors.transparent, // remove default margins
      builder: (context) {
        return Container(
          width: MediaQuery.of(context).size.width, // full width
          height: MediaQuery.of(context).size.height * 0.95, // almost full height
          decoration: const BoxDecoration(
            color: Colors.white,
            borderRadius: BorderRadius.vertical(top: Radius.circular(20)),
          ),
          child: Column(
            children: [
              // Top bar with close button
              Container(
                padding: const EdgeInsets.all(16),
                width: double.infinity,
                decoration: const BoxDecoration(
                  color: Colors.blue,
                  borderRadius: BorderRadius.vertical(top: Radius.circular(20)),
                ),
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text(
                      "${business.name} Products",
                      style: const TextStyle(
                          color: Colors.white, fontSize: 18, fontWeight: FontWeight.bold),
                    ),
                    IconButton(
                      icon: const Icon(Icons.close, color: Colors.white),
                      onPressed: () => Navigator.pop(context),
                    ),
                  ],
                ),
              ),
              Expanded(
                child: ListView.builder(
                  padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 8),
                  itemCount: business.products.length,
                  itemBuilder: (context, index) {
                    final product = business.products[index];
                    return Card(
                      margin: const EdgeInsets.symmetric(vertical: 8),
                      child: ListTile(
                        leading: Image.network(
                          product.imageUrl,
                          width: 50,
                          height: 50,
                          fit: BoxFit.cover,
                        ),
                        title: Text(product.name),
                        subtitle: Text("\$${product.price}"),
                        trailing: IconButton(
                          icon: const Icon(Icons.edit, color: Colors.blue),
                          onPressed: () {},
                        ),
                      ),
                    );
                  },
                ),
              ),
              Padding(
                padding: const EdgeInsets.all(16),
                child: ElevatedButton.icon(
                  onPressed: () {},
                  icon: const Icon(Icons.add),
                  label: const Text("Add Product"),
                  style: ElevatedButton.styleFrom(
                    backgroundColor: Colors.blue,
                    padding: const EdgeInsets.symmetric(vertical: 16),
                    minimumSize: const Size(double.infinity, 50),
                  ),
                ),
              ),
            ],
          ),
        );
      },
    );
  }


}